!function($){$(".display-posts-listing .excerpt-more").click(function(e){e.preventDefault();const me=$(this);let endpoint=wpApiSettings.root+"wp/v2/posts/",meta=me.closest(".listing-item").find("span:first");$.ajax({url:endpoint+meta.data("post-id"),method:"GET"}).done(function(response){me.closest(".listing-item").find(".excerpt").replaceWith($("<div>"+response.content.rendered+"</div>"))}).fail(function(response){console.log(response.responseJSON.message)})})}(jQuery);